import {Component, OnInit} from '@angular/core';
import pictures from './_files/pictures.json';
import {Picture} from "./pictures/piture.component";
import {HttpClient} from "@angular/common/http";
import {async} from "rxjs";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  constructor(private http: HttpClient) {
  }

  path: string = '../assets/pictures.json';
  pictures: Picture[] = [];

  getPictures() {
    return this.http.get<Picture[]>(this.path);
  }

  async ngOnInit() {
    await this.getPictures().subscribe(
      (response) => {
        this.pictures = response;
      })

  }

  isWhite: boolean = true;
  frameColor: string = this.isWhite ? "white" : "blue";
  displayVal: string = "";


  whiteBtnClick() {
    this.isWhite = true;
    this.frameColor = "white";
  }

  blueBtnClick() {
    this.isWhite = !true;
    this.frameColor = "blue";
  }

  chosenPic: string = '';
  picture: string = this.chosenPic;

  getValue(val: string) {
    this.displayVal = val;
  }

  updateSelection() {
    console.log(this.chosenPic);
    this.picture = this.chosenPic;
  }

  setDefault() {
    // this.picture=this.pictures[0].value;
  }
}

<!doctype html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport"
        content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">
  <meta http-equiv="X-UA-Compatible" content="ie=edge">

</head>
<body>
<section class="s1">

  <div class="main-container">
    <div class="blue-white-bar">

      <button class="bcg-btn"
              (click)="whiteBtnClick()"
              [ngClass]="{'selected' : isWhite}">
        White
      </button>


      <button class="bcg-btn"
              (click)="blueBtnClick()"
              [ngClass]="{'selected' : !isWhite}">
        Blue
      </button>

    </div>

    <div class="pic-and-txt">

      <div class="pic-dropdown">
        <select [(ngModel)]="chosenPic" (ngModelChange)="updateSelection()" class="pic-select" >
          <option value="none" disabled>Select picture</option>
          <option value="{{pic.value}}" *ngFor="let pic of pictures">{{pic.name}}</option>
        </select>

      </div>


      <div class="text-box">
        <input type="text" #box (keyup)="getValue(box.value)" placeholder="Enter Text" name="username">
      </div>
    </div>

    <div class="container">
      <div class="frame" [ngStyle]="{background:frameColor}">
        <div class="text-line">
          <h4>{{displayVal}}</h4>
        </div>
        <img src="{{picture }}" class="container-img" >
      </div>

      <!--      <canvas id="stage" width="640" height="360"></canvas>-->

    </div>
  </div>


</section>
</body>
</html>
